mapper.select:
SELECT que.idx, mem.id, mem.name, mem.agc_idx, agency.company_name, store.name AS store_name, que.cate, que.title, que.contents, que.status, que.regdate FROM notice_agency_que AS que, agency_member AS mem, agency AS agency, agency_store AS store WHERE que.agc_mem_idx = mem.idx AND mem.agc_idx = agency.idx AND agency.idx = store.agc_idx
AND agency.idx = #{agc_idx}
GROUP BY que.idx ORDER BY que.idx DESC

SELECT que.idx, mem.id, mem.name, mem.agc_idx, agency.company_name, store.name AS store_name, que.cate, que.title, que.contents, que.status, que.regdate FROM notice_agency_que AS que, agency_member AS mem, agency AS agency, agency_store AS store WHERE que.agc_mem_idx = mem.idx AND mem.agc_idx = agency.idx AND agency.idx = store.agc_idx
AND agency.idx = #{agc_idx}
AND store.idx = #{store_idx}
AND agency.company_num LIKE CONCAT('%', #{company_num}, '%')
ORDER BY que.idx DESC
