mapper.select:
SELECT info.agc_idx, info.agc_sto_idx as store_idx, info.box_idx, info.alias as box_name, info.description, info.cate, box.serial, box.id as box_id, box.status, box.serial, store.store_name, store.store_num, agency.company_name as agency_name, info.regdate
FROM sb_box_info as info JOIN sb_box as box ON info.box_idx = box.idx JOIN agency_store as store ON info.agc_sto_idx = store.idx JOIN agency as agency ON info.agc_idx = agency.idx
WHERE box.idx = #{idx}

mapper.insert:
  	insert into sb_box (
  		id, serial, status, regdate
    )
	values (
		#{box_id,jdbcType=VARCHAR}, #{serial,jdbcType=VARCHAR}, 0, #{regdate,jdbcType=TIMESTAMP}
	);

  	insert into sb_box_info (
  		box_idx, agc_idx, agc_sto_idx, alias, description, cate, regdate
    )
	values (
		#{box_idx,jdbcType=VARCHAR}, #{agc_idx,jdbcType=VARCHAR}, #{store_idx,jdbcType=VARCHAR},
		#{box_name,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, #{cate,jdbcType=VARCHAR},
		#{regdate,jdbcType=TIMESTAMP}
	)

mapper.update:
update sb_box set 
	id = #{box_id},
	serial = #{serial}, 
	status = 0,
	regdate = #{regdate}
WHERE idx = #{box_idx}
