mapper.select:
SELECT idx, agc_mem_idx, a.box_idx as box_idx, shp_id, gname, status, regdate, moddate, box_id, box_name, company_nm, agc_sto_name, agc_mem_id, agc_mem_name FROM (select idx, agc_mem_idx, box_idx, shp_id, gname, status, regdate, moddate from sb_inventory) a, (select idx as sb_box_idx, id as box_id from sb_box) b, (select box_idx, alias as box_name, agc_idx, agc_sto_idx from sb_box_info) c, (select idx as agc_idx, company_nm from agency) d, (select idx as agc_sto_idx, agc_idx, name as agc_sto_name from agency_store) e, (select idx as agc_member_idx, id as agc_mem_id, name as agc_mem_name from agency_member) f WHERE a.box_idx = b.sb_box_idx AND a.box_idx = c.box_idx AND c.agc_idx = d.agc_idx AND c.agc_idx = e.agc_idx AND c.agc_sto_idx = e.agc_sto_idx AND a.agc_mem_idx = f.agc_member_idx
AND a.idx = #{idx}
ORDER BY regdate desc
LIMIT #{skip}, #{take}

SELECT idx, goods_name, image, quantity, IF(box.box_used = 0, box.box_price, IF(sto.sto_used = 0, sto.sto_price, IF(agc.agc_used = 0, agc.agc_price, base.gds_price))) as price, FORMAT(IF(box.box_used = 0, box.box_price, IF(sto.sto_used = 0, sto.sto_price, IF(agc.agc_used = 0, agc.agc_price, base.gds_price))), 0) as price_form FROM (SELECT idx, box_idx, agc_mem_idx, agc_sto_idx, agc_idx, goods_skuid, gds_price, quantity, valid, regdate, image, goods_name, goods_idx FROM (select idx, box_idx, agc_mem_idx from sb_inventory) a, (select inv_idx, skuid, quantity, valid, regdate from sb_inventory_detail) b, (select skuid as goods_skuid, image, name as goods_name, idx as goods_idx, price as gds_price from sb_goods) c, (select box_idx as inf_box_idx, agc_sto_idx, agc_idx from sb_box_info) d WHERE 1 = 1
AND a.idx = #{idx}
AND a.idx = b.inv_idx AND b.skuid = c.goods_skuid AND a.box_idx = d.inf_box_idx ) base LEFT OUTER JOIN (select gds_idx, box_idx, used as box_used, price as box_price from sb_goods_box) box ON base.goods_idx = box.gds_idx AND base.box_idx = box.box_idx LEFT OUTER JOIN (select gds_idx, agc_sto_idx, used as sto_used, price as sto_price from sb_goods_store) sto ON base.goods_idx = sto.gds_idx AND base.agc_sto_idx = sto.agc_sto_idx LEFT OUTER JOIN (select gds_idx, agc_idx, used as agc_used, price as agc_price from sb_goods_agency) agc ON base.goods_idx = agc.gds_idx AND base.agc_idx = agc.agc_idx
